import React from 'react';
import './css/style.css';
import {
  useState,
  useEffect,
  useContext,
  createContext,
  useCallback
} from 'react';
import Pin from './image/bunbougu_hanko.cur';
import axios from 'axios';
import approvalIcon from './image/approval-icon.png';
const url = 'http://0.0.0.0:5000';
const Verificating = props => {
  const getParam = props.getParam;
  console.log(props);
  const [message, setMessage] = useState('');
  const [name, setName] = useState('');
  const [confirmNum, setConfirmNum] = useState('');
  const [verificationNum, setVerificationNum] = useState('');
  const [flag, setFlag] = useState(false);
  let Id = getParam('Id');
  useEffect(async () => {
    const params = {
      Id: Id
    };
    console.log(params);
    let res;
    try {
      res = await axios.get(url + '/getData', { params });
      setName(res.data.name);
      setMessage(res.data.message);
      setConfirmNum(res.data.confirmNum);
      setVerificationNum(res.data.verificationNum);
    } catch (error) {
      console.log(error);
    }
  }, [setMessage, setName, setConfirmNum, setVerificationNum]);
  const handleClick = async () => {
    console.log('rest');
    const params = {
      Id: Id
    };
    try {
      await axios.get(url + '/sub', { params });
      setFlag(true);
    } catch (error) {
      console.log(error);
    }
  };
  const style = {
    cursor: `url(${Pin}) 5 30, pointer`
  };
  const listItems = () => {
    if (flag == false) {
      return <img className="approvalIcon" src={approvalIcon} />;
    } else if (flag == true) {
      return '';
    }
  };
  if (name == '') {
    return <div>存在しないページです</div>;
  } else if (flag == false) {
    return (
      <div className="vertificationBody" style={style}>
        <div className="vertificationDocuments foldtl">
          <h1 className="verification">承認証</h1>
          <div className="Container">
            <p className="nameTitle">名前</p>
            <div className="name">{name}</div>
            <p className="messageTitle">メッセージ</p>
            <div className="messages">{message}</div>
            <div className="BoxOfVertification" onClick={handleClick}></div>
          </div>
        </div>
      </div>
    );
  } else if (flag == true) {
    return (
      <div className="vertificationBody" style={style}>
        <div className="vertificationDocuments foldtl">
          <h1 className="verification">承認証</h1>
          <div className="Container">
            <p className="nameTitle">名前</p>
            <div className="name">{name}</div>
            <p className="messageTitle">メッセージ</p>
            <div className="messages">{message}</div>
            <div className="BoxOfVertification">
              <img className="approvalIcon" src={approvalIcon} />
            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default Verificating;
