import React from 'react';
import './App.css';
import { useState, useEffect, useContext, createContext } from 'react';
import axios from 'axios';
const InputForm = () => {
  const ws = new WebSocket('ws://localhost:5000/pipe');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [count, setCount] = useState('');
  const [error, setError] = useState('');
  ws.onmessage = e => {
    console.log(e);
    if (e.data == 'error') {
      setError('この名前は登録されています');
    }
  };
  const handleChange = event => {
    switch (event.target.name) {
      case 'name':
        setName(event.target.value);
        break;
      case 'message':
        setMessage(event.target.value);
        break;
      case 'count':
        setCount(event.target.value);
        break;
      default:
        console.log('key not found');
    }
  };
  const handleSubmit = event => {
    event.preventDefault();
    const envelope = {
      name: name,
      message: message,
      count: count,
      oauth_token: getParam('oauth_token'),
      oauth_verifier: getParam('oauth_verifier')
    };
    ws.send(JSON.stringify(envelope));
    console.log(name);
  };
  return (
    <>
      <form onSubmit={handleSubmit}>
        <label>
          Message:
          <input
            type="text"
            name="message"
            value={message}
            onChange={handleChange}
          />
        </label>
        <label>
          Count:
          <input
            type="text"
            name="count"
            value={count}
            onChange={handleChange}
          />
        </label>
        <input type="submit" value="Submit" />
      </form>
      <div>{error}</div>
    </>
  );
};

//url無いの特定の要素を持ってくるためのコード(コピペ)
const getParam = (name, url) => {
  if (!url) url = window.location.href;
  name = name.replace(/[\[\]]/g, '\\$&');
  var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
    results = regex.exec(url);
  if (!results) return null;
  if (!results[2]) return '';
  return decodeURIComponent(results[2].replace(/\+/g, ' '));
};

const AccessOauth = () => {
  const ws = new WebSocket('ws://localhost:5000/pipe');
  const [userData, setUserData] = useState('');
  const [data, setData] = useState('');
  // サーバー側からメッセージが送られてきた際に受け取り、関数を発動する(Twitterの認証用URLに飛ぶため)
  ws.onmessage = e => {
    console.log(e);
    if (e.data.includes('oauth_token')) {
      window.location.href = e.data;
    } else {
      console.log(e);
      setData(e.data);
    }
  };
  //クリックした時(今回は文字をbuttonをクリックしたらサーバーにTwitterのもじが送られる)
  const handleClick = () => {
    console.log('rest');
    ws.send('Twitter');
  };
  return (
    <>
      <button onClick={handleClick}>Twitter認証</button>
    </>
  );
};

const BottonContent = () => {
  const [message, setMessage] = useState('');
  const [name, setName] = useState('');
  const axios = require('axios');
  const url = '';
  let flag = false;
  useEffect(async () => {
    try {
      const res = await axios.get(url);
      const items = res.data;
      setName(items.name);
      setMessage(items.message);
    } catch (error) {
      const { status, statusText } = error.response;
      console.log(`Error! HTTP Status: ${status} ${statusText}`);
    }
  });
  const handleClick = async () => {
    console.log('rest');
    axios.post('/sub', {
      nameId: getParam('name'),
      time: getParam('time')
    });
    flag = true;
  };
  if (flag == false) {
    return (
      <>
        <p>{name}</p>
        <p>{message}</p>
        <button onClick={handleClick}>承認</button>
      </>
    );
  } else if (flag == true) {
    return (
      <>
        <p>Thank you your corporation!! </p>
      </>
    );
  }
};
function App() {
  const [flag, setFlag] = useState(false);
  let render;
  // if (window.location.search.includes('name')) {
  //   render = <BottonContent />;
  // }
  // webSocketとの通信;
  // レンダー前にwsがopenした後にurl内のverifierを返す;
  if (window.location.search.includes('verifier')) {
    render = <InputForm />;
    //サーバへ送信
    // ws.send(JSON.stringify(envelope));
  } else if (
    window.location.search.includes('nameId') &&
    window.location.search.includes('time')
  ) {
    render = <BottonContent />;
  } else {
    render = <AccessOauth />;
  }

  // //レンダー要素の切替
  // let render = flag ? <InputForm /> : <AccessOauth />;
  return render;
}

export default App;
