import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [flag, setFlag] = useState(false);
  const ws = new WebSocket('ws://localhost:5000/pipe');
  // constructor(props){
  //   super(props)
  //   this.state = {
  //     showFlag: false
  //   }
  // }
  let userData = '';
  useEffect(() => {
    ws.onopen = function(event) {
      if (window.location.search.includes('verifier')) {
        setFlag(true);
        userData = "getParam('oauth_verifier')";
        ws.send(getParam('oauth_verifier'));
      }
    };
  })
  function getParam(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, '\\$&');
    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
      results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, ' '));
  }

  ws.onmessage = function(e) {
    console.log(e);
    if (e.data.includes('oauth_token')) {
      window.location.href = e.data;
    }
  };

  function handleClick() {
    console.log('rest');
    ws.send('Twitter');
  }

  let render = flag ? (
    <div>{userData}</div>
  ) : (
    <button onClick={handleClick}>連携アプリ認証</button>
  );
  // useEffect(async () => {
  //   const result = await axios('https://cee83dd0.ngrok.io/twitter_auth');

  //   windowLogin = window.open(result.data.url);
  //   // setTimeout(function(){CheckLoginStatusTwitter(history)}, 1000)
  //   const xx = windowLogin.focus();
  //   console.log(xx);
  //   // setData(result);
  // });
  return <>{render}</>;
}

export default App;
