import React from 'react';
import './App.css';
import { useState, useEffect } from 'react';
import { useForm } from 'react-hook-form';

function accessOauth(handleClick) {
  return <button onClick={handleClick}>連携アプリ認証</button>;
}

function textInput(props) {
  return (
    <form onSubmit={props.handleSubmit(props.onSubmit)}>
      <label>
        Name:
        <input type="text" name="name" ref={props.register} />
      </label>
      <label>
        Message:
        <input type="text" name="message" ref={props.register} />
      </label>
      <label>
        Count:
        <input type="text" name="count" ref={props.register} />
      </label>
      <input type="submit" value="Submit" />
    </form>
  );
}
function App() {
  const [flag, setFlag] = useState(false);
  const [userData, setUserData] = useState('');
  const [data, setData] = useState('');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [count, setCount] = useState('');
  const { register, handleSubmit } = useForm();
  const onSubmit = data => {
    console.log(data);
  };
  //webSocketとの通信
  const ws = new WebSocket('ws://localhost:5000/pipe');
  // レンダー前にwsがopenした後にurl内のverifierを返す
  // useEffect(() => {
  //   ws.onopen = event => {
  //     if (userData == false && window.location.search.includes('verifier')) {
  //       console.log('useEffect');
  //       setFlag(true);
  //       console.log(typeof getParam('oauth_token'));
  //       const envelope = {
  //         oauth_token: getParam('oauth_token'),
  //         oauth_verifier: getParam('oauth_verifier')
  //       };
  //       //サーバへ送信
  //       ws.send(JSON.stringify(envelope));
  //     }
  //   };
  //   setUserData('true');
  // });

  //url無いの特定の要素を持ってくるためのコード(コピペ)
  function getParam(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, '\\$&');
    var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
      results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, ' '));
  }

  // サーバー側からメッセージが送られてきた際に受け取り、関数を発動する(Twitterの認証用URLに飛ぶため)
  ws.onmessage = e => {
    console.log(e);
    if (e.data.includes('oauth_token')) {
      window.location.href = e.data;
    } else {
      console.log(e);
      setData(e.data);
    }
  };
  //クリックした時(今回は文字をbuttonをクリックしたらサーバーにTwitterのもじが送られる)
  function handleClick() {
    console.log('rest');
    ws.send('Twitter');
  }
  console.log(flag);

  //レンダー要素の切替
  let render = flag ? <textInput {...props} /> : <accessOauth {...props} />;
  return <>{render}</>;
}

export default App;
