import React from 'react';
import logo from './logo.svg';
import './App.css';
import { useState, useEffect } from 'react';
import axios from 'axios';

function App() {
  const [data, setData] = useState('');
  var ws = new WebSocket('ws://localhost:8080/pipe');

  ws.onmessage = function(e) {
    document.getElementById('text-field').innerHTML = e.data;
  };
  useEffect(async () => {
    const result = await axios('https://cee83dd0.ngrok.io/twitter_auth');

    windowLogin = window.open(result.data.url);
    // setTimeout(function(){CheckLoginStatusTwitter(history)}, 1000)
    const xx = windowLogin.focus();
    console.log(xx);
    // setData(result);
  });
  return (
    <>
      <button onClick={this.handleClick}>連携アプリ認証</button>
    </>
  );
}

export default App;
