import React from 'react';
import './css/style.css';
import {
  useState,
  useEffect,
  useContext,
  createContext,
  useCallback
} from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import useReactRouter from 'use-react-router';
import { useSpring, useTransition, animated as a } from 'react-spring';
import axios from 'axios';
import Pin from './image/bunbougu_hanko.png';
import AccessOauth from './AccessOauth.js';
import TwitterInputForm from './TwitterInputForm.js';
import Verificated from './Verificated.js';
import Verificating from './Verificating.js';
const url = 'http://0.0.0.0:5000';

//url無いの特定の要素を持ってくるためのコード(コピペ)
const getParam = (name, url) => {
  if (!url) url = window.location.href;
  name = name.replace(/[\[\]]/g, '\\$&');
  var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
    results = regex.exec(url);
  if (!results) return null;
  if (!results[2]) return '';
  return decodeURIComponent(results[2].replace(/\+/g, ' '));
};

function App() {
  return (
    <Router>
      <Switch>
        <Route path="/" component={AccessOauth} exact />
        <Route path="/twitterForm" component={TwitterInputForm} exact />
        <Route path="/verificated" component={Verificated} exact />
        <Route path="/verificating" component={Verificating} exact />
      </Switch>
    </Router>
  );
}

export default App;
