import React from 'react';
import './css/style.css';
import {
  useState,
  useEffect,
  useContext,
  createContext,
  useCallback
} from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import useReactRouter from 'use-react-router';
import { useSpring, useTransition, animated as a } from 'react-spring';
import axios from 'axios';
import Pin from './image/bunbougu_hanko.png';
const url = 'http://0.0.0.0:5000';
const Verificating = () => {
  const [message, setMessage] = useState('');
  const [name, setName] = useState('');
  const [confirmNum, setConfirmNum] = useState('');
  const [verificationNum, setVerificationNum] = useState('');
  const [flag, setFlag] = useState(false);
  const axios = require('axios');
  const url = 'http://localhost:5000';
  let Id = getParam('Id');
  useEffect(async () => {
    const params = {
      Id: Id
    };
    console.log(params);
    let res;
    try {
      res = await axios.get(url + '/getData', { params });
      setName(res.data.name);
      setMessage(res.data.message);
      setConfirmNum(res.data.confirmNum);
      setVerificationNum(res.data.verificationNum);
    } catch (error) {
      console.log(error);
    }
  }, [setMessage, setName, setConfirmNum, setVerificationNum]);
  const handleClick = async () => {
    console.log('rest');
    const params = {
      Id: getParam('Id')
    };
    try {
      await axios.get(url + '/sub', { params });
      setFlag(true);
    } catch (error) {
      console.log(error);
    }
  };
  if (name == '') {
    return <div>存在しないページです</div>;
  } else if (flag == false) {
    return (
      <div className="vertificationBody" style={{ cursor: `url(${Pin})` }}>
        <div className="vertificationDocuments foldtl">
          <h1 className="verification">承認証</h1>
          <div className="Container">
            <p className="nameTitle">名前</p>
            <div className="name">{name}</div>
            <p className="messageTitle">メッセージ</p>
            <div className="messages">{message}</div>
            <div className="BoxOfVertification" onClick={handleClick}></div>
          </div>
        </div>
      </div>
    );
  } else if (flag == true) {
    return (
      <div className="vertificationBody" style={{ cursor: `url(${Pin})` }}>
        <div className="vertificationDocuments foldtl">
          <h1 className="verification">承認証</h1>
          <div className="Container">
            <p className="nameTitle">名前</p>
            <div className="name">{name}</div>
            <p className="messageTitle">メッセージ</p>
            <div className="messages">{message}</div>
            <div className="BoxOfVertification">
              <img></img>
            </div>
          </div>
        </div>
        <div>Thank you your corporation!! </div>
      </div>
    );
  }
};

export default Verificating;
