import React from 'react';
import './css/style.css';
import {
  useState,
  useEffect,
  useContext,
  createContext,
  useCallback
} from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import useReactRouter from 'use-react-router';
import { useSpring, useTransition, animated as a } from 'react-spring';
import axios from 'axios';
const TwitterInputForm = () => {
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [count, setCount] = useState('');
  const [error, setError] = useState('');
  const [greetingStatus, displayGreeting] = useState(false);
  const contentProps = useSpring({
    opacity: greetingStatus ? 1 : 0,
    marginLeft: greetingStatus ? 0 : 1000
  });
  let cookies = document.cookie; //全てのcookieを取り出して
  let cookiesArray = cookies.split(';'); // ;で分割し配列に
  console.log(cookies);
  for (var c of cookiesArray) {
    //一つ一つ取り出して
    let cArray = c.split('='); //さらに=で分割して配列に
    if (cArray[0] == 'userId') {
      // 取り出したいkeyと合致したら
      setName(cArray[1]);
    }
  }

  const contentProp = useSpring({
    opacity: greetingStatus ? 0 : 1,
    marginRight: greetingStatus ? 2000 : 0
  });
  //   ws.onmessage = e => {
  //     console.log(e);
  //     if (e.data == 'error') {
  //       setError('この名前は登録されています');
  //     } else if (e.data.includes('name')) {
  //       let url = window.location.href;
  //       window.location.href = url.split('?')[0] + e.data;
  //     }
  //   };
  //   const confirms = () => {
  //     if (count == '') {
  //       setError('空です。入力してください');
  //       displayGreeting(false);
  //     } else {
  //       setError('');
  //       displayGreeting(true);
  //     }
  //   };
  const handleChange = event => {
    switch (event.target.name) {
      case 'name':
        setName(event.target.value);
        break;
      case 'message':
        setMessage(event.target.value);
        break;
      case 'count':
        setCount(event.target.value);
        break;
      default:
        console.log('key not found');
    }
  };
  //   const confirmCount = () => {
  //     if (count == '') {
  //       setError('入力してください');
  //     } else {
  //       displayGreeting(true);
  //     }
  //   };
  const handleSubmit = async event => {
    if (message == '' || count == '') {
      setError('messageを入力してください');
    } else {
      event.preventDefault();
      let params = new URLSearchParams();
      params.append('type', 'Twitter');
      params.append('message', 'message');
      params.append('count', 'count');
      // const envelope = {
      //   type: 'Twitter',
      //   name: name,
      //   message: message,
      //   count: count,
      //   oauth_token: getParam('oauth_token'),
      //   oauth_verifier: getParam('oauth_verifier')
      // };
      try {
        let res = await axios.post(
          'http://0.0.0.0:5000' + '/push_data',
          params
        );
        console.log('OK');
      } catch (error) {
        console.log(error);
      }
    }
  };
  return (
    <>
      {' '}
      <div class="vertificationDocuments foldtl">
        <h1 className="verification">承認証</h1>
        <div className="Container">
          <p className="nameTitle">名前</p>
          <div className="name">{name}</div>
          <p className="messageTitle">メッセージ</p>
          <textarea
            name="message"
            value={message}
            onChange={handleChange}
          ></textarea>
          <div className="vertificationNum">
            <p className="messageTitle">何人に確認して欲しいか？</p>
            <input
              className="vertificationNumber"
              value={count}
              onChange={handleChange}
              type="number"
              name="count"
            />
          </div>
          <button onClick={handleSubmit}>決定</button>
        </div>
      </div>
    </>
  );
};

