import React from 'react';
import './App.css';
import { useState, useEffect, useContext, createContext } from 'react';

// const accessOauth = () => {
//   return <button onClick={handleClick}>連携アプリ認証</button>;
// };
const InputForm = () => {
  const ws = new WebSocket('ws://localhost:5000/pipe');
  const [name, setName] = useState('');
  const [message, setMessage] = useState('');
  const [count, setCount] = useState('');

  const handleChange = event => {
    switch (event.target.name) {
      case 'name':
        setName(event.target.value);
        break;
      case 'message':
        setMessage(event.target.value);
        break;
      case 'count':
        setMessage(event.target.value);
        break;
      default:
        console.log('key not found');
    }
  };
  const handleSubmit = event => {
    //url無いの特定の要素を持ってくるためのコード(コピペ)
    const getParam = (name, url) => {
      if (!url) url = window.location.href;
      name = name.replace(/[\[\]]/g, '\\$&');
      var regex = new RegExp('[?&]' + name + '(=([^&#]*)|&|#|$)'),
        results = regex.exec(url);
      if (!results) return null;
      if (!results[2]) return '';
      return decodeURIComponent(results[2].replace(/\+/g, ' '));
    };
    event.preventDefault();
    const envelope = {
      name: name,
      message: message,
      count: count,
      oauth_token: getParam('oauth_token'),
      oauth_verifier: getParam('oauth_verifier')
    };
    ws.send(JSON.stringify(envelope));
    console.log(name);
  };
  return (
    <form onSubmit={handleSubmit}>
      <label>
        Name:
        <input type="text" name="name" value={name} onChange={handleChange} />
      </label>
      <label>
        Message:
        <input
          type="text"
          name="message"
          value={message}
          onChange={handleChange}
        />
      </label>
      <label>
        Count:
        <input
          type="text"
          name="count"
          value={message}
          onChange={handleChange}
        />
      </label>
      <input type="submit" value="Submit" />
    </form>
  );
};

const AccessOauth = () => {
  const ws = new WebSocket('ws://localhost:5000/pipe');
  const [userData, setUserData] = useState('');
  const [data, setData] = useState('');
  // サーバー側からメッセージが送られてきた際に受け取り、関数を発動する(Twitterの認証用URLに飛ぶため)
  ws.onmessage = e => {
    console.log(e);
    if (e.data.includes('oauth_token')) {
      window.location.href = e.data;
    } else {
      console.log(e);
      setData(e.data);
    }
  };
  //クリックした時(今回は文字をbuttonをクリックしたらサーバーにTwitterのもじが送られる)
  const handleClick = () => {
    console.log('rest');
    ws.send('Twitter');
  };
  return <button onClick={handleClick}>Twitter認証</button>;
};

function App() {
  const [flag, setFlag] = useState(false);

  // webSocketとの通信;
  // レンダー前にwsがopenした後にurl内のverifierを返す;
  useEffect(() => {
    if (flag == false && window.location.search.includes('verifier')) {
      setFlag(true);
      //サーバへ送信
      // ws.send(JSON.stringify(envelope));
    }
  });

  //レンダー要素の切替
  let render = flag ? <InputForm /> : <AccessOauth />;
  return render;
}

export default App;
